package Lec5;

public class Data_Demo_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		System.out.println(ch);
		ch++;// ch = (char)(ch+1)
		System.out.println(ch);
		ch += 1;
		System.out.println(ch);
		// ch = ch + 1;
		int i = 1;
		i++;// i = (int)(i+1)
		i += 1;// i = (int)(i+1)
	}

}
